mapper.select:
SELECT f.field_id, f.field_label, f.location, afd.legal_land_id, ll.other_description, ll.primary_property_identifier, MAX(afd.crop_year) AS max_crop_year FROM field f join annual_field_detail afd on f.field_id = afd.field_id left join legal_land ll on ll.legal_land_id = afd.legal_land_id WHERE afd.crop_year <= #{cropYear} AND #{cropYear} BETWEEN f.active_from_crop_year AND COALESCE(f.active_to_crop_year, #{cropYear})
AND afd.legal_land_id = #{legalLandId}
AND afd.field_id = #{fieldId}
AND UPPER(f.location) like #{fieldLocation}
GROUP BY f.field_id, f.field_label, f.location, afd.legal_land_id, ll.other_description, ll.primary_property_identifier HAVING MAX(afd.crop_year) = ( SELECT MAX(a.crop_year) FROM annual_field_detail a WHERE a.crop_year <= #{cropYear} AND a.field_id = f.field_id ) ORDER BY field_label


SELECT 	f.field_id, 
		f.field_label, 
		MAX(afd.crop_year) AS max_crop_year
FROM field f
JOIN annual_field_detail afd ON f.field_id = afd.field_id
WHERE afd.legal_land_id = #{legalLandId}
AND afd.crop_year  <=  #{cropYear}
AND exists (SELECT 1
			FROM contracted_field_detail cfd
			JOIN grower_contract_year gcy ON gcy.grower_contract_year_id = cfd.grower_contract_year_id
			JOIN policy p ON p.contract_id = gcy.contract_id and p.crop_year = gcy.crop_year
			WHERE cfd.annual_field_detail_id = afd.annual_field_detail_id

				AND p.contract_id = #{includeContractId}


				AND p.contract_id  <>  #{excludeContractId}

			)

	AND f.field_id  <>  #{excludeFieldId}

GROUP BY f.field_id, f.field_label
ORDER BY field_label
