mapper.select:
, gmtc.description, cc.insurance_plan_id, (select count(dycc.declared_yield_contract_commodity_guid) from declared_yield_contract_commodity dycc join declared_yield_contract dyc on dyc.declared_yield_contract_guid = dycc.declared_yield_contract_guid where dycc.grade_modifier_type_code = gm.grade_modifier_type_code and dyc.crop_year = gm.crop_year and dycc.crop_commodity_id = gm.crop_commodity_id ) as total_used FROM grade_modifier gm JOIN grade_modifier_type_code gmtc on gmtc.grade_modifier_type_code = gm.grade_modifier_type_code JOIN crop_commodity cc on cc.crop_commodity_id = gm.crop_commodity_id WHERE gm.crop_year = #{cropYear} AND cc.insurance_plan_id = #{insurancePlanId}
AND cc.crop_commodity_id = #{cropCommodityId}
ORDER BY gm.crop_commodity_id, gmtc.description, gm.grade_modifier_type_code
